function showProvince(){var list=eval(json_provice),html="";$.each(list,function(i,t){html+=" <li onclick=\"showCityOfProvince('"+t.code+"','"+t.name+'\')"><a href="javascript:;">'+t.name+"</a></li>"}),$("#provinceDiv").append(html),showhidediv("province2")}function showCityOfProvince(p_code,p_name){document.getElementById("province").value=p_name;var city_list=eval(json_city),city_html="";$.each(city_list,function(i,t){t.code.substr(0,2)==p_code.substr(0,2)&&(city_html+=" <li onclick=\"showDistrictOfCity('"+t.code+"','"+t.name+'\')"><a href="#">'+t.name+"</a></li>")}),$("#cityDiv").empty(),$("#cityDiv").append(city_html),showhidediv("city2")}function showDistrictOfCity(city_code,city_name){document.getElementById("city").value=city_name;var district_list=eval(json_district),district_html="";$.each(district_list,function(i,t){t.code.substr(0,4)==city_code.substr(0,4)&&(district_html+=" <li onclick=\"okProvinceCity('"+t.code+"','"+t.name+'\')"><a href="#">'+t.name+"</a></li>")}),$("#districtDiv").empty(),$("#districtDiv").append(district_html),showhidediv("district2")}function okProvinceCity(i,t){document.getElementById("district").value=t,showhidediv("province2"),showhidediv("city2"),showhidediv("district2")}function backdiv(i){1==i?showhidediv("province2"):2==i?showhidediv("city2"):3==i&&showhidediv("district2")}function showhidediv(i){var t=document.getElementById(i);t&&("block"==t.style.display?t.style.display="none":t.style.display="block")}